function Build(id) {
	id = +id;
	if (id >= 0) return this.getAttributes(id);
}

utils.extend(Build, Element);

// получение информации о здании 
Build.prototype.getAttributes = function(id) {
	this.id = id;
	for (var i in lib.builds[this.id]) {
		this[i] = lib.builds[this.id][i];
	}

	return this;
};

/**
 * библиотечные методы
 */

Build.prototype.getId = function(){
	return this.id;
}
 
Build.prototype.getType = function() {
	return this.type;
};

Build.prototype.getMaxCount = function() {
	return this.maxcount;
}

Build.prototype.isEmpty = function() {
	return this.getId() == Build.no;
}

// кол-во юнитов, которые тренируются в здании
Build.prototype.getTakesUnitsCount = function() {
	return this.unitstrain? this.unitstrain.length: 0;
};


// юниты, которые тренируются в здании
Build.prototype.getTakesUnits = function() {
	return this.unitstrain;
};

// здание, в которое можно перестроить
Build.prototype.getNextBuild = function(race) {
	return this.next[race];
};

Build.prototype.getTime = function(level) {
	return utils.oddFunc(this.buildtime, level) * 3600;
};

Build.prototype.getPay = function(level, afrikan) {
	return utils.oddFunc(this.pay, level) * (afrikan ? lib.town.production.afrikaeconomy : 1);
};

// проверка здания на доступность
Build.prototype.enabled = function() {
	return this.maxcount > 0;
};

// проверка на чудо
Build.prototype.isWonder = function() {
	return this.constructor.isWonder(this.id);
};

// стоимость постройки в ресурсах для конкретного уровня
Build.prototype.getCostLevel = function(level) {
	var cost = [];
	for (var res in this.cost[0]) {
		// if (typeof cost[res] == 'undefined') cost[res] = 0;
		cost[res] = parseInt(utils.oddFunc([this.cost[0][res], this.cost[1][res], this.cost[2][res], this.cost[3]], level), 10);
	}
	return cost;
};

Build.prototype.toSlot = function(){
	var slot = new Slot(this.getId(), 1);
	
	slot.fillPop();
	
	return slot;
};

Build.prototype.getAvailQuestLevel = function(){
	var scienceList = ScienceList.getAll();
	var freeLevel = lib.build.maxlevel;
	for (var science in scienceList.getList()){
		science = scienceList.getElem(science);
		freeLevel -= science.getBuildLevel(this.getId());
	}
	return freeLevel;
}

// получение радиуса действия чуда света
Build.prototype.getWonderRadius = function() {
	return this.wonderradius;
};


Build.prototype.getTakesProductionsCount = function() {
	if (this.productres) {
		return this.productres.length;
	}
	return 0;
};

Build.prototype.getTakesProduction = function(resource) {
	return this.productres ? this.productres[resource] : 0;
};

