Build.prototype.getProductres = function (){
	var resList = new ResList();
	
	for (var item in this.productres){
		var item = this.productres[item];
		
		resList.addRes(item.res, 1);
	}
	
	return resList;
};

Build.prototype.getEfficiency = function(resource) {
	return this.efficiency || 0;
};

Build.prototype.getProductresByType = function() {
	var prodTypeList = {},
		resList = this.getProductres().getList();
	
	for(var res in resList){
		res = resList[res];
		
		if( !prodTypeList[res.getProdtype()] )
			prodTypeList[res.getProdtype()] = [];
		
		prodTypeList[res.getProdtype()].push(res);
	}
	
	return prodTypeList;
};

/**
 * статические методы
 */

// проверка на чудо
Build.isWonder = function(id) {
	return (lib.builds[id]||{}).slot == Build.slotType.wonder;
};

// получение названия здания
Build.prototype.getName = function(id) {
	return this._getAddLib().name;
};

Build.prototype.getDesc = function(id) {
	return this._getAddLib().desc;
};

Build.prototype.getSpeciale = function(id) {
	return this._getAddLib().speciale||'';
};

// получение всех зданийV
Build.getAll = function() {
	return lib.builds;
};

Build.getResearchLevel = function(id) {
	var build = wofh.account.research.build||[];
	return build[id]||0;
};

Build.getTakesProduction = function(id, resource) {
	var production = lib.builds[id].productres;
	return production ? production[resource] : 0;
};

Build.getGroup = function(id) {
	//if( id ) return Build.typeGroup.other;
	
	switch(lib.builds[id].type) {
		case Build.type.wonder:
			return Build.typeGroup.wonder;
		case Build.type.fake:
		case Build.type.hide:
		case Build.type.embassy:
		case Build.type.administration:
		case Build.type.corruption:
		case Build.type.ecology:
			return Build.typeGroup.other;
		case Build.type.store:
			return Build.typeGroup.store;
		case Build.type.production:
			var build = new Build(id);
			if (build.getProductres().hasElem(Resource.ids.science)) return Build.typeGroup.science;
			return Build.typeGroup.production;
		case Build.type.prodBoost:
			return Build.typeGroup.production;
		case Build.type.train:
		case Build.type.waterarmyspeed:
		case Build.type.airarmyspeed:
			return Build.typeGroup.train;
		case Build.type.grown:
			return Build.typeGroup.grown;
		case Build.type.culture:
			return Build.typeGroup.culture;
		case Build.type.trade:
		case Build.type.watertradespeed:
			return Build.typeGroup.trade;
		case Build.type.defence:
		case Build.type.airdef:
			return Build.typeGroup.defence;
	}
};


Build.getWonderList = function(){
	var list = [];
	for (var id in lib.wonder) {
		if (Build.getGroup(id) == 0 && lib.builds[id].slot != Build.slotType.no) {
			var build = new Build(id);
			
			list.push(build);
		}
	}
	return list;
};

Build.prototype.getImgOffset = function (isCorrect, isNewTown) {
    if (typeof(isNewTown) == 'undefined') {
        isNewTown = true;
    }
	var off = Build.lib[this.id][isNewTown ? 'dNew' : 'd'];
	var x, y;
	if (this.isWonder()) {
		x = 0;
		y = 0;
	} else if (isNewTown) {
		x = isCorrect ? off[0] + 2 : off[0];
		y = isCorrect ? off[1] + 40 : off[1];
	} else {
		x = isCorrect ? off[0] + 105 : off[0];
		y = isCorrect ? off[1] + 95 : off[1];
	}
	// console.log('x:', off[0], 'y:', off[1], 'x2:', x, 'y2:', y, 'isCorrect:', isCorrect, 'town:', isNewTown);
	return {x: x, y: y};
};

Build.prototype.getRaces = function (){
	return new Races(this.race);
};


Build.prototype.getConflict = function (){
	return this.conflict;
};

Build.prototype.isHidden = function (){
	return this._getAddLib().hidden;
};

Build.init = function() {
	this.calcConflicts();
};



Build.calcConflicts = function() {
	for (var buildId in lib.builds) {
		var buildLib = lib.builds[buildId];
		
		if (!buildLib.conflict) buildLib.conflict = {};
		
		for (var buildNextId in buildLib.next){
			var buildNextId = buildLib.next[buildNextId];
			if (+buildNextId == Build.no) continue;
			var buildNextLib = lib.builds[buildNextId];
			
			if (!buildNextLib.conflict) buildNextLib.conflict = {};
			
			buildLib.conflict[buildNextId] = 1;
			buildNextLib.conflict[buildId] = 1;
            /*
            for (var i in buildNextLib.conflict){
                if (i != buildId) {
                    buildLib.conflict[i] = 1;
                }
            }
            for (var i in buildLib.conflict){
                if (i != buildNextId) {
                    buildNextLib.conflict[i] = 1;
                }
            }*/
		}
	}
};

